module.exports = (function () {
    var mongoose = require('mongoose');
    var ObjectId = mongoose.Schema.Types.ObjectId;

    var productSchema = new mongoose.Schema({
        wTrack: { type: String, ref: 'wTrack', default: null },
        canBeSold: { type: Boolean, default: true },
        canBeExpensed: { type: Boolean, default: true },
        eventSubscription: { type: Boolean, default: true },
        canBePurchased: { type: Boolean, default: true },
        imageSrc: { type: String, default: 'data:image/png;base64,' },
        name: { type: String, default: '' },
        info: {
            productType: { type: String, ref: 'productTypes', default: null },
            salePrice: {type: Number, default: 1.00},
            isActive: {type: Boolean, default: true},
            barcode: {type: String, default: ''},
            description: {type: String, default: ''}
        },
        accounting: {
            category: {
                _id: { type: ObjectId, ref: 'ProductCategory', default: null },
                name: {type: String, default: ''}
            }
        },
        workflow: { type: ObjectId, ref: 'workflows', default: null },
        whoCanRW: { type: String, enum: ['owner', 'group', 'everyOne'], default: 'everyOne' },
        groups: {
            owner: { type: ObjectId, ref: 'Users', default: null },
            users: [{ type: ObjectId, ref: 'Users', default: null }],
            group: [{ type: ObjectId, ref: 'Department', default: null }]
        },
        creationDate: { type: Date, default: Date.now },
        createdBy: {
            user: { type: ObjectId, ref: 'Users', default: null },
            date: { type: Date, default: Date.now }
        },
        editedBy: {
            user: { type: ObjectId, ref: 'Users', default: null },
            date: { type: Date, default: Date.now }
        }
    }, { collection: 'Products' });

    mongoose.model('Product', productSchema);

    if(!mongoose.Schemas) {
        mongoose.Schemas = {};
    }

    mongoose.Schemas['Products'] = productSchema;
})();