var express = require('express');
var router = express.Router();

var Handler = require('../../handlers/outlet');
module.exports = function (db) {
    var handler = new Handler(db);

    function checkAuth(req, res, next) {
        if (req.session && req.session.loggedIn) {
            next();
        } else {
            res.send(401);
        }
    }

    /**
     * __Type__ `GET`
     *
     * Base ___url___ for build __requests__ is `http:/<host>:<port>/mobile/outlet`
     *
     * Returns the all existing `outlet`
     * @see {@link OutletModel}
     * @returns {OutletModel[]}
     * @example
     * REQUEST:
     *     'http://localhost:9797/mobile/outlet'
     * RESPONSE
     *     [{
	 *          "_id" : ObjectId("55f967bc298e739963000036"),
	 *          "editedBy" : {
	 *              "user" : null
	 *          },
	 *          "createdBy" : {
	 *	            "date" : ISODate("2015-09-16T12:59:40.544Z"),
	 *	            "user" : null
	 *          },
	 *          "isOwn" : true,
	 *          "branches" : [
	 *              null
	 *          ],
	 *          "description" : "Greatest wooden stuff selling stock in whole Middle-Earth",
	 *          "imageSrc" : "data:image/png;base64,",
	 *          "manager" : null,
	 *          "country" : null,
	 *          "name" : "Wooden dream",
	 *          "__v" : 0
     *      }, ...]
     * @method /outlet
     * @instance
     */
    router.get('/', checkAuth, handler.getAll);
    router.get('/getForDD', checkAuth, handler.getForDD);

    /**
     * __Type__ 'GET'
     *
     * Base ___url___ for build __requests__ is `http:/<host>:<port>/mobile/outlet`
     *
     * Returns existing `outlet` with requested id
     * @see {@link OutletModel}
     * @returns {OutletModel}
     * @example
     * REQUEST:
     *     'http://localhost:9797/mobile/outlet/55eeb7b58f9c1deb19000002'
     * RESPONSE
     *     {
	 *          "_id" : ObjectId("55f967bc298e739963000036"),
	 *          "editedBy" : {
	 *              "user" : null
	 *          },
	 *          "createdBy" : {
	 *	            "date" : ISODate("2015-09-16T12:59:40.544Z"),
	 *	            "user" : null
	 *          },
	 *          "isOwn" : true,
	 *          "branches" : [
	 *              null
	 *          ],
	 *          "description" : "Greatest wooden stuff selling stock in whole Middle-Earth",
	 *          "imageSrc" : "data:image/png;base64,",
	 *          "manager" : null,
	 *          "country" : null,
	 *          "name" : "Wooden dream",
	 *          "__v" : 0
     *      }
     * @method /outlet/:id
     * @instance
     */
    router.get('/:id', checkAuth, handler.getById);

    /**
     * __Type__ 'POST'
     *
     * Base ___url___ for build __requests__ is `http:/<host>:<port>/mobile/outlet`
     *
     * Creates new outlet.
     *
     * @example
     * REQUEST:
     *     'http://localhost:9797/mobile/outlet/'
     * BODY:
     * {
     *      name: "Vopak",
     *      imageSrc: "data:image/png;base64,",
     *      country: 55f967bc298e739963000036,
     *      manager: 55fa95b36d2106f34a00000a,
     *
     * }
     * RESPONSE :
     * {
     *   _id : "55eeb7b58f9c1deb19000008"
     * }
     *
     * @method /outlet/create
     * @instance
     */
    router.post('/', checkAuth, handler.create);
    router.post('/getBy/', checkAuth, handler.getBy);
    /**
     * __Type__ 'GET'
     *
     * Base ___url___ for build __requests__ is `http:/<host>:<port>/mobile/outlet`
     *
     * Returns existing `outlet` with requested id
     * @see {@link OutletModel}
     * @returns {OutletModel}
     * @example
     * REQUEST:
     *     'http://localhost:9797/mobile/outlet/55eeb7b58f9c1deb19000002'
     * RESPONSE
     *     {
	 *          "_id" : ObjectId("55f967bc298e739963000036"),
	 *          "editedBy" : {
	 *              "user" : null
	 *          },
	 *          "createdBy" : {
	 *	            "date" : ISODate("2015-09-16T12:59:40.544Z"),
	 *	            "user" : null
	 *          },
	 *          "isOwn" : true,
	 *          "branches" : [
	 *              null
	 *          ],
	 *          "description" : "Greatest wooden stuff selling stock in whole Middle-Earth",
	 *          "imageSrc" : "data:image/png;base64,",
	 *          "manager" : null,
	 *          "country" : null,
	 *          "name" : "Wooden dream",
	 *          "__v" : 0
     *      }
     * @method /outlet/:id
     * @instance
     */
    router.put('/:id', handler.update);

    /**
     * __Type__ `PATCH`
     *
     * Base ___url___ for build __requests__ is `http:/<host>:<port>/mobile/outlet`
     *
     * Updated outlet with specific id. Put into body only properties to update
     * @see {@link OutletModel}

     * @example
     * REQUEST:
     *     'http://localhost:9797/mobile/outlet/55eeb7b58f9c1deb19000009'
     * BODY
     *     {
     *          "name" : "another outlet"
     *     }
     * RESPONSE status
     * @method /outlet/:id
     * @instance
     */
    router.patch('/:id', handler.update);

    return router;
};